import axios from 'axios';

const API_URL = 'http://localhost:5000/api/smartphones';

class SmartphoneService {
  // Récupérer tous les smartphones
  getAllSmartphones() {
    return axios.get(API_URL);
  }

  // Récupérer un smartphone par ID
  getSmartphoneById(id) {
    return axios.get(`${API_URL}/${id}`);
  }

  // Ajouter un smartphone
  ajouterSmartphone(smartphone) {
    return axios.post(API_URL, smartphone);
  }

  // Modifier un smartphone
  modifierSmartphone(id, smartphone) {
    return axios.put(`${API_URL}/${id}`, smartphone);
  }

  // Supprimer un smartphone
  supprimerSmartphone(id) {
    return axios.delete(`${API_URL}/${id}`);
  }
}

const smartphoneServiceInstance = new SmartphoneService();
export default smartphoneServiceInstance;
